import 'dotenv/config';
import { Options } from 'sequelize';

const config: Options = {
  username: process.env.DB_USER || 'root',
  password: process.env.DB_PASS || '',
  database: process.env.DB_NAME || 'database_example', // substitua o nome do banco quando for utilizar em seu projeto!
  host: process.env.DB_HOST || 'localhost',
  port: Number(process.env.DB_PORT) || 3306,
  dialect: 'mysql',
};

export = config;

import express from 'express';
import carsRouter from './routes/CarRouter';
import pieceRouter from './routes/PieceRouter';
import adminRouter from './routes/AdminRouter';
import employeeRouter from './routes/EmployeeRouter';
import clientRouter from './routes/ClientRouter';

class App {
  public app: express.Express;

  constructor() {
    this.app = express();

    this.config();

    // Não remover essa rota
    this.app.get('/', (req, res) => res.json({ ok: true }));
  }

  private config():void {
    const accessControl: express.RequestHandler = (_req, res, next) => {
      res.header('Access-Control-Allow-Origin', '*');
      res.header('Access-Control-Allow-Methods', 'GET,POST,DELETE,OPTIONS,PUT,PATCH');
      res.header('Access-Control-Allow-Headers', '*');
      next();
    };

    this.app.use(express.json());
    this.app.use(accessControl);
    this.routes();
  }

  private routes() {
    this.app.use(carsRouter);
    this.app.use(pieceRouter);
    this.app.use(adminRouter);
    this.app.use(employeeRouter);
    this.app.use(clientRouter);
  }

  public start(PORT: string | number): void {
    this.app.listen(PORT, () => console.log(`Running on port ${PORT}`));
  }
}

export default App;

