import { Request, Response } from 'express';
import CarService from '../services/Car.service';
import mapStatusHTTP from '../utils/mapStatusHTTP';

export default class CarController {
  constructor(private carService = new CarService()) {}

  async listCars(_req: Request, res: Response) {
    const { status, data } = await this.carService.listCars();
    return res.status(mapStatusHTTP(status)).json(data);
  }

  async insertCar(req: Request, res: Response) {
    const { name, brand, year } = req.body;
    const { status, data } = await this.carService.insertCar({ name, year, brand });
    return res.status(mapStatusHTTP(status)).json(data);
  }

  async removeCar(req: Request, res: Response) {
    const { name, brand, year } = req.body;
    const { status, data } = await this.carService.removeCar({ name, year, brand });
    return res.status(mapStatusHTTP(status)).json(data);
  }

  async findCar(req: Request, res: Response) {
    const { name } = req.query;
    const { status, data } = await this.carService.findCar(name as string);
    return res.status(mapStatusHTTP(status)).json(data);
  }

  async updateCar(req: Request, res: Response) {
    const { id, name, brand, year } = req.body;
    const { status, data } = await this.carService.updateCar({ id, name, brand, year });
    return res.status(mapStatusHTTP(status)).json(data);
  }
}

import { ICar } from '../interfaces/databaseModels/ICar';
import CarModel from '../models/CarModel';
import { ServiceResponse } from '../utils/mapStatusHTTP';

export default class CarService {
  constructor(private carModel = new CarModel()) {}

  async listCars(): Promise<ServiceResponse<ICar[]>> {
    const result = await this.carModel.listCars();
    return { status: 'SUCCESSFUL', data: result };
  }

  async insertCar({ name, brand, year }: Omit<ICar, 'id'>): Promise<ServiceResponse<ICar>> {
    const result = await this.carModel.insertCar({ name: name.toUpperCase(), brand, year });
    return { status: 'CREATED', data: result };
  }

  async removeCar({ name, brand, year }: Omit<ICar, 'id'>):
  Promise<ServiceResponse<Omit<ICar, 'id'>>> {
    const result = await this.carModel.removeCar({ name, year, brand });
    if (result === 0) {
      return { status: 'NOT_FOUND', data: { message: 'carro não encontrado' } };
    }

    return { status: 'SUCCESSFUL', data: { name, brand, year } };
  }

  async findCar(name: string): Promise<ServiceResponse<ICar[]>> {
    const result = await this.carModel.findCar(name);
    return { status: 'SUCCESSFUL', data: result };
  }

  async updateCar({ id, name, brand, year }: ICar): Promise<ServiceResponse<ICar>> {
    await this.carModel.updateCar({ id, name: name.toUpperCase(), brand, year });

    return { status: 'SUCCESSFUL', data: { id, name: name.toUpperCase(), brand, year } };
  }
}

import { Op } from 'sequelize';
import SequelizeCar from '../database/models/SequelizeCar';
import { ICar } from '../interfaces/databaseModels/ICar';
import { ICarModel } from '../interfaces/ICarModel';

export default class CarModel implements ICarModel {
  private model = SequelizeCar;

  async listCars(): Promise<ICar[]> {
    const result = await this.model.findAll();
    return result;
  }

  async insertCar({ name, brand, year }: { name: string; brand: string; year: number; }):
  Promise<ICar> {
    const [result] = await this.model.findOrCreate({ where: { name, brand, year } });
    return result;
  }

  async removeCar({ name, brand, year }: Omit<ICar, 'id'>): Promise<number> {
    const result = await this.model.destroy({ where: { name, brand, year } });
    return result;
  }

  async findCar(name: string): Promise<ICar[]> {
    const result = await this.model.findAll({ where: {
      name: { [Op.like]: `%${name.toUpperCase()}%` },
    } });
    return result;
  }

  async updateCar({ id, name, brand, year }: ICar): Promise<number> {
    const [result] = await this.model.update({ name, brand, year }, { where: { id } });
    return result;
  }
}

